mapper.select:
SELECT board.*, (SELECT COUNT(*) FROM FileInfo WHERE boardId = board.id) AS fileCnt, (SELECT COUNT(*) FROM PhotoInfo WHERE boardId = board.id) AS pictureCnt, (SELECT title FROM BoardType WHERE id = board.boardType) AS boardName, (SELECT COUNT(*) FROM Reply WHERE boardId = board.id) AS replyCnt, usr.username AS writerName, (SELECT url FROM PhotoInfo where boardId = board.id LIMIT 0,1) AS thumbnail FROM Board board LEFT JOIN users usr ON (board.writer = usr.id)
( board.boardType = 1 or board.boardType = 2 or board.boardType = 3 or board.boardType = 4 or board.boardType = 5 )
board.boardType = ${boardType}
and ( board.title like '%${query}%' or board.content like '%${query}%' )
order by board.id desc LIMIT ${from}, ${pageSize}

SELECT COUNT(*) FROM Board
boardType = 1 or boardType = 2 or boardType = 3 or boardType = 4 or boardType = 5
boardType = ${boardType}
